import React, { useState, FormEvent } from 'react'
import { FiChevronRight} from 'react-icons/fi'
import api from '../../services/api'

import logoImg from '../../assets/logo.svg'

import { Title, Form, Repositories} from './styles'

interface Repository {
    full_name: string
    description: string
    owner: {
        login: string
        avatar_url: string
    }
}

const Dashboard: React.FC = () => {
    const [newRepo, setNewRepo] = useState('')
    const [repositories, setRepositories] = useState<Repository[]>([])

    async function handleAddRepository(event: FormEvent<HTMLFormElement>,
        ):Promise<void> {
        event.preventDefault()

        const response = await api.get<Repository>(`repos/${newRepo}`)

        const repositor = response.data

        setRepositories([...repositories, repository])
    }

    return (

        <>
            <img src={logoImg} alt="Github Explorer" />
            <Title> Explore repositorios no Github</Title>

            <Form onSubmit={handleAddRepository} >
                <input
                value={newRepo} 
                onChange= {(e): void => setNewRepo(e.target.value)}
                placeholder="Digite o nome do repositorio"
                />
                <button type="submit">Pesquisar</button>
            </Form>

            <Repositories>
            <a href="teste">
                <img src="https://avatars1.githubusercontent.com/u/52171813?s=460&u=ec20e0d8cca41b8a516ebabcd36849863b178adc&v=4" 
                alt="Leonardo C Hably"
                />

                <div>
                    <strong>Py_TextGame</strong>
                    <p>âš”A Text-based Game made in Python </p>
                </div>

                <FiChevronRight size={20} />
            </a>

            </Repositories>

        </>

    )
}

export default Dashboard
